// Guardar el msg.payload en una variable global
global.set('dbData', msg.payload);

// Puedes agregar este mensaje si quieres verificar el almacenamiento
msg.info = "Datos guardados globalmente en 'dbData'";
return msg;

// Obtener la variable global
let dbdatos = global.get("dbData") || []; // Asegurarse de que exista

// Obtener la hora actual en formato HH:MM
let now = new Date();
let currentHour = now.getHours().toString().padStart(2, "0");
let currentMinute = now.getMinutes().toString().padStart(2, "0");
let currentTime = `${currentHour}:${currentMinute}`;
node.warn(currentTime);

// Convertir una hora HH:MM a minutos totales para cálculos
function timeToMinutes(time) {
    let [hour, minute] = time.split(":").map(Number);
    return hour * 60 + minute;
}

// Calcular el tiempo actual en minutos
let currentTimeMinutes = timeToMinutes(currentTime);

// Filtrar los registros
let porIniciar = [];
let iniciada = [];
let noIniciada = [];

dbdatos.forEach(record => {
    let inicioMinutes = timeToMinutes(record.hora_inicio);
    let finMinutes = timeToMinutes(record.hora_fin);

    // Por Iniciar: Diferencia de menos de 45 minutos hasta la hora de inicio
    if (inicioMinutes - currentTimeMinutes <= 45 && inicioMinutes > currentTimeMinutes) {
        porIniciar.push(record);
    }
    // Iniciada: La hora actual está entre hora_inicio y hora_fin
    else if (currentTimeMinutes >= inicioMinutes && currentTimeMinutes <= finMinutes) {
        iniciada.push(record);
    }
    // No Iniciada: El resto de los registros
    else {
        noIniciada.push(record);
    }
});

// Registrar las listas en variables globales
global.set('PorIniciar', porIniciar);
global.set('Iniciada', iniciada);
global.set('NoIniciada', noIniciada);

// Imprimir en la consola de Node-RED para depuración
node.warn({ PorIniciar: porIniciar });
node.warn({ Iniciada: iniciada });
node.warn({ NoIniciada: noIniciada });

// Retornar el mensaje original
return msg;

// Obtener la lista global de clases "Por Iniciar"
let porIniciar = global.get("PorIniciar") || [];

// Extraer el número de sala del tópico
let sala = msg.topic.split("/")[0];

// Verificar si la sala está en la lista "Por Iniciar"
let salaEnLista = porIniciar.some(record => record.sala_real == sala);

if (salaEnLista) {
    return msg; // Dejar pasar el mensaje
} else {
    return null; // Bloquear el mensaje
}

// Umbral de temperatura ideal
const tempIdeal = 21;
const tolerancia = 1; // Margen de +/- 1°C

// Obtener temperatura actual
let temperaturaActual = parseFloat(msg.payload);
let sala = msg.topic.split("/")[0];

// Decidir si encender el aire acondicionado
if (temperaturaActual < (tempIdeal - tolerancia) || temperaturaActual > (tempIdeal + tolerancia)) {
    return {
        payload: "ON",
        topic: sala
    };
} else {
    return null; // No hacer nada si la temperatura está dentro del rango
}

// Obtener la lista global de clases "Iniciadas"
let iniciada = global.get("Iniciada") || [];

// Extraer el número de sala del tópico
let sala = msg.topic.split("/")[0];

// Verificar si la sala está en la lista "Iniciadas"
let salaEnLista = iniciada.some(record => record.sala_real == sala);

// Si la sala no está en "Iniciadas", bloquear el mensaje
if (!salaEnLista) {
    return null;
}

// Umbral de temperatura
const tempIdeal = 21;
const tolerancia = 1; // Margen de +/- 1°C

// Obtener el tipo de dato (presencia o temperatura) del tópico
let tipo = msg.topic.split("/")[1];

// Obtener el estado actual de la sala del contexto de flujo
let estado = flow.get(sala) || { temperatura: null, presencia: "0", contadorAusencia: 0 };

// Actualizar el estado según el tipo de dato recibido
if (tipo === "temp") {
    estado.temperatura = parseFloat(msg.payload); // Guardar temperatura
} else if (tipo === "pres") {
    estado.presencia = msg.payload; // Guardar presencia ("1" para presente, "0" para ausente)
}

// Decisiones:
if (estado.presencia.includes("Stationary target")) {

        return {
            payload: "ON",
            topic: sala // Enviar comando "ON" al tópico de la sala
        };

} else {
    // Si no hay presencia, incrementar el contador de ausencia
    estado.contadorAusencia += 1;

    // Si el contador alcanza o supera los 10 ciclos, enviar "OFF"
    if (estado.contadorAusencia >= 10) {
        estado.contadorAusencia = 0; // Reiniciar el contador
        flow.set(sala, estado); // Guardar el estado actualizado

        return {
            payload: "OFF",
            topic: sala // Enviar comando "OFF" al tópico de la sala
        };
    }
}

// Guardar el estado actualizado
flow.set(sala, estado);

// No hacer nada si no se cumple ninguna condición
return null;

// Obtener la lista global de clases "No Iniciada"
let noIniciada = global.get("NoIniciada") || [];

// Obtener el número de sala desde el tópico
let sala = msg.topic.split("/")[0];

// Verificar si la sala está en la lista "No Iniciada"
let salaEnLista = noIniciada.some(record => record.sala_real == sala);

if (salaEnLista) {
    return msg; // Dejar pasar el mensaje
} else {
    return null; // Bloquear el mensaje
}

// Obtener el mensaje de presencia ("1" para presente, "0" para ausente)
let presencia = msg.payload; // Datos del sensor de presencia

let sala = msg.topic.split("/")[0]; // Número de sala
node.warn(sala);
// Obtener el estado actual de la sala desde el contexto de flujo
let estado = flow.get(sala) || { contadorAusencia: 0, ultimaPresencia: "0" };

if (presencia.includes("Stationary target")) {
    // Si hay presencia
    estado.contadorAusencia = 0; // Reiniciar el contador de ausencia
    estado.ultimaPresencia = "1"; // Actualizar el estado de presencia

    // Guardar el estado actualizado
    flow.set(sala, estado);

    // Enviar comando "ON"
    return {
        payload: "ON",
        topic: sala
    };
} else {


        return {
            payload: "OFF",
            topic: sala
        };
    }

    // Guardar el estado actualizado
    flow.set(sala, estado);

    // No hacer nada aún si no se alcanza el umbral
    return null;
